const dropdownSelect=(()=>{let r=document.querySelectorAll('[data-bs-toggle="select"]');for(let e=0;e<r.length;e++){let t=r[e].querySelectorAll(".dropdown-item"),l=r[e].querySelector(".dropdown-toggle-label"),o=r[e].querySelector('input[type="hidden"]');for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){e.preventDefault();e=this.querySelector(".dropdown-item-label").innerText;l.innerText=e,null!==o&&(o.value=e)})}})();export default dropdownSelect;
