const masterCheckbox=(()=>{const t=document.querySelectorAll("[data-master-checkbox-for]");if(0!==t.length)for(let e=0;e<t.length;e++)t[e].addEventListener("change",function(){let e=document.querySelector(this.dataset.masterCheckboxFor),t=e.querySelectorAll('input[type="checkbox"]');if(this.checked)for(let e=0;e<t.length;e++)t[e].checked=!0,t[e].dataset.checkboxToggleClass&&document.querySelector(t[e].dataset.bsTarget).classList.add(t[e].dataset.checkboxToggleClass);else for(let e=0;e<t.length;e++)t[e].checked=!1,t[e].dataset.checkboxToggleClass&&document.querySelector(t[e].dataset.bsTarget).classList.remove(t[e].dataset.checkboxToggleClass)})})();export default masterCheckbox;
